#!/usr/bin/env python3\n\"\"\"\n换脸路径配置测试脚本\n专门测试路径查找和模型检测逻辑\n\"\"\"\n\nimport os\nimport sys\n\n# 添加当前目录到路径\ncurrent_dir = os.path.dirname(os.path.abspath(__file__))\nif current_dir not in sys.path:\n    sys.path.insert(0, current_dir)\n\ndef test_faceswap_path_detection():\n    \"\"\"测试faceswap路径检测\"\"\"\n    print(\"🔍 测试faceswap路径检测功能...\")\n    \n    possible_paths = [\n        \"/runpod-volume/faceswap\",  # RunPod主要路径\n        \"/Users/baileyli/Documents/AI同志项目/image generation/faceswap\",  # 本地开发\n        \"/app/faceswap\",  # Docker容器\n        \"/workspace/faceswap\",  # RunPod备用\n        \"../faceswap\",  # 相对路径\n        \"./faceswap\"  # 当前目录\n    ]\n    \n    print(\"📁 检查可能的faceswap路径:\")\n    found_paths = []\n    for path in possible_paths:\n        exists = os.path.exists(path)\n        status = \"✅\" if exists else \"❌\"\n        print(f\"  {status} {path}\")\n        if exists:\n            found_paths.append(path)\n    \n    if found_paths:\n        print(f\"\\n✅ 找到 {len(found_paths)} 个有效路径\")\n        return found_paths[0]  # 返回第一个找到的路径\n    else:\n        print(\"\\n❌ 未找到任何有效的faceswap路径\")\n        return None\n\ndef main():\n    \"\"\"主测试函数\"\"\"\n    print(\"🚀 换脸路径配置测试开始\")\n    print(\"=\"*60)\n    \n    # 测试路径检测\n    found_path = test_faceswap_path_detection()\n    \n    print(\"\\n\" + \"=\"*60)\n    print(\"🎉 测试完成\")\n\nif __name__ == \"__main__\":\n    main()" 